import { useState } from 'react';
import './user-auth.css';
import AuthJWT from './AuthJWT.js';

export default function Login () {

    console.log("is authenticated: ", AuthJWT());

    const [jwt, setJWT] = useState({
        "refresh": "",
        "access": ""
    });

    const [userData, setUserData] = useState({
        "username": "",
        "password": "",
    });

    const cleanUserData = () => {
        setUserData({
            "username": "",
            "password": "",
        });
    }

    const [loginError, setLoginError] = useState({});

    const changeHandler = (event) => {
        setUserData({
            ...userData, 
            [event.target.name]: event.target.value
        });

        console.log(event.target.value);
    }

    const submitHandler = async (event) => {
        event.preventDefault();

        fetch('http://127.0.0.1:8000/auth/jwt/create/', {
            method: 'POST', 
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userData)
        })
        .then(response => {
            if (!response.ok) {
                return response.json()
                .then(data => {throw new Error(JSON.stringify(data))})
            } else {
                console.log("Successfully logged in!")
                // alert("Successfully logged in!");
                // cleanUserData();
            }
            return response.json();
        })
        .then(data => {
            console.log(data.refresh);
        })
        .catch(error => {
            console.log(error.message);
        })
    }    

    return (
        <div>
            <form onSubmit={submitHandler}> 
                <div>
                    <label>Username</label>  
                    <input type="text" name="username" onChange={changeHandler} value={userData.username} />  
                </div>
                <div>
                    <label>Password</label>  
                    <input type="password" name="password" onChange={changeHandler} value={userData.password} />  
                </div>
                <button type="submit">Login</button>   
            </form>
        </div>
    )
}
