import {useState} from 'react';

export default function AuthJWT() {

    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [jwt, setJWT] = useState(JSON.parse(localStorage.getItem('jwt')));

    const checkJWT = (token) => {
        fetch("http://localhost:8000/auth/jwt/verify/", {
            method: 'GET', 
            authentication: `JWT ${token}`
        })
    }

    if (jwt) {
        console.log("JWT exists");
        fetch("http://localhost:8000/auth/users/", {
            method: 'GET', 
            headers: {
                'Authorization': `JWT ${jwt.access}`,
                'Content-Type': 'application/json'
            }
        })
        .then( response => {
            if (response.ok) {
                setIsAuthenticated(true);
            } else {
                // refresh token 
                fetch("http://localhost:8000/auth/jwt/refresh/", {
                    method: 'POST', 
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        "refresh": jwt.refresh
                    })
                })
                .then( response => {
                    if (response.ok) {
                        console.log("successfully refreshed token");
                        setJWT((prevJWT) => ({
                            ...prevJWT, 
                            "access": response.access
                        }))
                    }
                })
            }
        })

    }

    // if (!jwt) {
    //     console.log("JWT does not exist");
    // }

    // console.log("jwt in localStorage: ", jwt);

    return isAuthenticated;
}
