import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const wordVariants = {
    highlight: {
      color: 'blue', // Highlight color when visible
      opacity: 1,
    },
    hidden: {
      color: 'transparent', // Hide the word initially
      opacity: 0,
    },
  };


const Sentence = () => {
  const [currentWord, setCurrentWord] = useState('real estate experts'); // Initial word
  const words = ['real estate experts', 'everyone']; // Words to cycle through

  useEffect(() => {
    const intervalId = setInterval(() => {
      const nextIndex = (words.indexOf(currentWord) + 1) % words.length;
      setCurrentWord(words[nextIndex]);
    }, 3000); // Change word every 3 seconds

    return () => clearInterval(intervalId); // Cleanup on unmount
  }, [currentWord, words]);

  return (
    <div>
      Build a modern database for: 
      <motion.span animate="highlight" variants={wordVariants}>
        {currentWord}
      </motion.span>
    </div>
  );
};

export default Sentence;
